import styled from 'styled-components';

const Navbar = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: fixed;
  top: 0;
  width: 100%;
  height: 60px;
  background-color: white;
  z-index: 1;
  padding: 0 10px; /* Reduce horizontal padding */
  transition: background-color 0.3s ease-in-out;
  &:hover {
    background-color: #f2f2f2;
  }
`;

const Logo = styled.img`
  height: 40px;
`;

const NavMenu = styled.ul`
  display: flex;
  list-style: none;
  margin: 0;
  padding: 0;
  height: 100%;
  align-items: center;
`;

const NavMenuItem = styled.li`
  margin: 0 5px; /* Reduce margin between nav items */
  height: 100%;
  display: flex;
  align-items: center;
  &:hover {
    background-color: #f2f2f2;
  }

  @media (max-width: 768px) { /* Reduce font size for smaller screens */
    font-size: 0.8rem;
  }
`;

const NavLink = styled.a`
  color: black;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

export default function Navigation() {
  return (
    <Navbar>
      <Logo src="/logo.png" alt="Logo" />
      <NavMenu>
        <NavMenuItem><NavLink href="#">Home</NavLink></NavMenuItem>
        <NavMenuItem><NavLink href="#">About</NavLink></NavMenuItem>
        <NavMenuItem><NavLink href="#">Services</NavLink></NavMenuItem>
        <NavMenuItem><NavLink href="#">Contact</NavLink></NavMenuItem>
      </NavMenu>
    </Navbar>
  );
}
