import React, { useState } from 'react';
import SearchForm from "./SearchForm";

const BusinessSearchPage = () => {
    const [searchParams, setSearchParams] = useState({
        city: '',
        state_abreviation: '',
        name: '',
        postal_code: false,
        stars: false,
        review_count: false,
        is_open: false,
        accepts_insurance: false,
        by_appointment_only: false,
        accept_creditcard: false,
        garage_parking: false,
        lot_parking: false,
        street_parking: false,
        valet_parking: false,
        bYOB: false,
        open_24_hours: false,
        accepts_bitcoin: false,
        dogs_allowed: false,
        drive_thru: false,
        delivery: false,
        outdoor_seating: false,
        good_for_dancing: false,
        happy_hour: false,
        take_out: false,
        reservations: false,
        wifi: false,
        good_for_groups: false,
        casual: false,
        upscale: false,
        trendy: false,
        touristy: false,
        divey: false,
        hipster: false,
        classy: false,
        intimate: false,
        romantic: false,
        alcohol: false,
    });

    const [searchResults, setSearchResults] = useState([]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setSearchParams((prevParams) => ({
            ...prevParams,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Make API request here
        const response = await fetch(`/businesses/search?${new URLSearchParams(searchParams).toString()}`);
        const data = await response.json();
        setSearchResults(data);
    };

    return (
        <div>
            <h1>Business Search</h1>
            <SearchForm searchParams={searchParams} handleChange={handleChange} handleSubmit={handleSubmit} />

            <h2>Search Results</h2>
            <ul>
                {searchResults.map((business) => (
                    <li key={business.id}>
                        {business.name} - {business.stars} stars
                    </li>
                ))}
            </ul>
        </div>
    );
};


export default BusinessSearchPage;
