  }

  return (
    <nav className="navbar">
    <div className="navbar-logo">Logo</div>
    <div
      className={`navbar-menu ${menuActive ? 'active' : ''}`}>
      <a href="/">Home</a>
      <a href="/">About</a>
      <a href="/">Contact</a>
    </div>
    <div className="navbar-toggle" onClick={handleToggleMenu}>
    <span></span>
            <span></span>
            <span></span>
    </div>
    </nav>
  );
}




