import React, { useState } from 'react';

const BusinessSearchPage = () => {
    const [searchParams, setSearchParams] = useState({
        city: '',
        state_abreviation: '',
        accepts_insurance: false,
        // Add other search parameters here
    });

    const [searchResults, setSearchResults] = useState([]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setSearchParams((prevParams) => ({
            ...prevParams,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Make API request here
        const response = await fetch(`/businesses/search?${new URLSearchParams(searchParams).toString()}`);
        const data = await response.json();
        setSearchResults(data);
    };

    return (
        <div>
            <h1>Business Search</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="city">City:</label>
                <input type="text" name="city" value={searchParams.city} onChange={handleChange} />
                <br />

                <label htmlFor="state_abreviation">State:</label>
                <input type="text" name="state_abreviation" value={searchParams.state_abreviation} onChange={handleChange} />
                <br />

                <label htmlFor="accepts_insurance">Accepts Insurance:</label>
                <input type="checkbox" name="accepts_insurance" checked={searchParams.accepts_insurance} onChange={handleChange} />
                <br />

                {/* Add other search parameters as needed */}

                <button type="submit">Search</button>
            </form>

            <h2>Search Results</h2>
            <ul>
                {searchResults.map((business) => (
                    <li key={business.id}>
                        {business.name} - {business.stars} stars
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BusinessSearchPage;
