import { useEffect, useState } from 'react'
import { Box, Container, Pagination } from '@mui/material'
import { NavLink } from 'react-router-dom'
import Navbar from '../components/NavBar'
import '../components/styles.css'
// import profile_icon from './profile_icon.png'

const config = require('../config.json')

export default function UsersPage() {
    const pageSize = 20
    const [users, setUsers] = useState([])
    const [page, setPage] = useState(1)
    const totalPages = Math.ceil(users.length / pageSize)


    useEffect(() => {
        fetch(
          `http://${config.server_host}:${config.server_port}/users?page=${page}&page_size=${pageSize}`,
        )
          .then((res) => res.json())
          .then((resJson) => setUsers(resJson))
      }, [page, pageSize])



    const bodyStyle = {
        backgroundImage: `url('./glass.jpeg')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center center',
        height: '100vh',
        color: 'white',
        margin: 100,
        paddingTop: '60px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
      }

      const userCard = {
        width: '100%',
        height: '50%',
        border: '1px solid lightgrey',
        borderRadius: '60px',
        margin: '10px',
        padding: '10px',
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'flex-start',
        transition: 'all .2s ease-in-out',
      }

      const handlePageChange = (e, newPage) => {
        // Can always go to previous page (TablePagination prevents negative pages)
        // but only fetch next page if we haven't reached the end (currently have full page of data)
        if (newPage < page && users.length === pageSize) {
          // Note that we set newPage + 1 since we store as 1 indexed but the default pagination gives newPage as 0 indexed
          setPage(newPage + 1)
        }
      }

      const userCardHover = {
        transform: 'scale(1.05)',
        boxShadow: '2px 2px 10px lightgrey',
      }

      const flexFormat = {
        display: 'flex',
        flexDirection: 'column',
        flexWrap: 'wrap',
        justifyContent: 'space-evenly',
      }

      return (
        <div>
        <div style={bodyStyle}>
          <Navbar/>
              <Container style={flexFormat}>
                  {users
                      .slice((page - 1) * pageSize, page * pageSize)
                      .map((user) => (
                        <NavLink to = {`/users/${user.user_id}`}>
                          <Box key={user.user_id} style={userCard} sx = {{ ':hover': userCardHover }}>                          
                              <img src={require('./profile_icon.png')} width= '50px' height = '50px' style={{verticalAlign: 'middle'}}/>
                              <h2 style={{ color: '#1A1A1A', margin: '20px 20px 10px', borderRight: '6px light blue'}}>
                                {user.name}
                              </h2>
                              <h2 style = {{margin: '20px 20px 10px'}}>
                                Joined: {user.yelping_since}
                              </h2>
                              <h2 style = {{margin: '20px 20px 10px'}}>
                                Average stars: {user.average_stars}
                              </h2>
                          </Box>
                        </NavLink>
                      ))}
              </Container>
              <Pagination
                  count={totalPages}
                  page={page}
                  onChange={handlePageChange}
                  color="primary"
              />
        </div>
        </div>
      );
}
