import { useState, useRef,  } from 'react';
import './styles.css';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const navbarMenuRef = useRef(null);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    if (navbarMenuRef.current) {
      if (isOpen) {
        navbarMenuRef.current.style.height = `${navbarMenuRef.current.scrollHeight}px`;
      } else {
        navbarMenuRef.current.style.height = '0px';
      }
    }
  }, [isOpen]);

  return (
    <nav className="navbar">
      <div className="navbar-logo">Review Roam</div>
      <div className="navbar-toggle" onClick={toggleMenu}>
        <span></span>
        <span></span>
        <span></span>
      </div>
      <div className={`navbar-menu ${isOpen ? 'active' : ''}`} ref={navbarMenuRef}>
        <a href="/">Home</a>
        <a href="/">About</a>
        <a href="/">Services</a>
        <a href="/">Contact</a>
      </div>
    </nav>
  );
}

export default Navbar;
