import { useState, useEffect, useRef } from 'react';
import './styles.css';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [menuHeight, setMenuHeight] = useState(0);

  const navRef = useRef(null);

  useEffect(() => {
    setMenuHeight(navRef.current.scrollHeight);
  }, [isOpen]);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">Review Roam</div>
      <div className="navbar-toggle" onClick={toggleMenu}>
        <span></span>
        <span></span>
        <span></span>
      </div>
      <div className={`navbar-menu ${isOpen ? 'active' : ''}`} style={{ height: menuHeight }} ref={navRef}>
        <a href="/">Home</a>
        <a href="/">About</a>
        <a href="/">Services</a>
        <a href="/">Contact</a>
      </div>
    </nav>
  );
}

export default Navbar;
