import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
const config = require('../config.json')

export default function oneBusinessPage() {
  const { business_id } = useParams()
  const [business, setbusiness] = useState([])
  const [reviews, setReviews] = useState([])
  const [tips, setTips] = useState([])
  const [hours, setHours] = useState([])

  useEffect(() => {
    fetch(
      `http://${config.server_host}:${config.server_port}/business/${business_id}`,
    )
      .then((res) => res.json())
      .then((resJson) => setbusiness(resJson))

    fetch(
      `http://${config.server_host}:${config.server_port}/business/${business_id}/fivereviews`,
    )
      .then((res) => res.json())
      .then((resJson) => setReviews(resJson))
    fetch(
      `http://${config.server_host}:${config.server_port}/business/${business_id}/fivetips`,
    )
      .then((res) => res.json())
      .then((resJson) => setTips(resJson))

    fetch(
        `http://${config.server_host}:${config.server_port}/business/${business_id}/hours`,
      )
        .then((res) => res.json())
        .then((resJson) => setHours(resJson))
  }, [business_id])

  return (
    <h2>Welcome</h2>
  );
}
